SELECT c.customernumber,COUNT(pl.productline) FROM customers AS c JOIN orders AS o ON c.customerNumber = o.customerNumber JOIN orderdetails as od ON o.orderNumber = od.orderNumber JOIN products as p on od.productCode = p.productCode JOIN productlines as pl on p.productLine = pl.productLine WHERE pl.productline = 'Classic Cars' GROUP by c.customernumber HAVING COUNT(pl.productline) > 23;